library(peacesciencer)
library(dplyr)

conflict_df &lt;- create_stateyears(system = 'gw') |&gt;
  filter(year %in% c(1946:1999)) |&gt;
  add_ucdp_acd(type=c("intrastate"), only_wars = FALSE) |&gt;
  add_democracy() |&gt;
  add_creg_fractionalization() |&gt;
  add_sdp_gdp() |&gt;
  add_rugged_terrain()

glimpse(conflict_df)




